package grg.model.cars;

import java.util.ArrayList;
import java.util.Date;
import java.util.List;

import org.springframework.web.bind.annotation.CrossOrigin;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

@CrossOrigin(origins= "*")
@RequestMapping("/v5")
@RestController


public class CarController {
	
	@GetMapping("/cars")
	public List<Car> findCars(){
		List<Car> cars= new ArrayList<>();
		Car car1= new Car(1,"Volkswagen","Gti","White",45000,"data:image/jpeg;base64,",new Date());
		Car car2= new Car(2,"Toyota","Supra","Red",55000,"https://dogtime.com/assets/uploads/2011/03/puppy-development.jpg",new Date());
		
		cars.add(car1);
		cars.add(car2);
		
		return cars;
		
	}

}


